import React from "react";
import ReactDOM from "react-dom";
import { WithContext as ReactTags } from "react-tag-input";
import axios from "axios";
const KeyCodes = {
  comma: 188,
  enter: 13,
};

const delimiters = [KeyCodes.comma, KeyCodes.enter];

class Input extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      tags: [{ id: "pinout", text: "pinout" }],
      model: [],
      placeholder: "add component name",
      placeholder1: "add searching tags",
      suggestions: [],
    };
    this.handleDelete = this.handleDelete.bind(this);
    this.handleAddition = this.handleAddition.bind(this);
    this.handleAddition = this.m_handleAddition.bind(this);
    this.handleDrag = this.handleDrag.bind(this);
  }

  handleDelete(i) {
    const { tags } = this.state;
    this.setState({
      tags: tags.filter((tag, index) => index !== i),
    });
  }

  handleAddition(tag) {
    this.setState((state) => ({ tags: [...state.tags, tag] }));
  }

  handleDrag(tag, currPos, newPos) {
    const tags = [...this.state.tags];
    const newTags = tags.slice();

    newTags.splice(currPos, 1);
    newTags.splice(newPos, 0, tag);

    // re-render
    this.setState({ tags: newTags });
  }

  m_handleDelete(i) {
    const { model } = this.state;
    this.setState({
      model: model.filter((tag, index) => index !== i),
    });
  }

  m_handleAddition(tag) {
    this.setState((state) => ({ model: [...state.model, tag] }));
  }

  m_handleDrag(tag, currPos, newPos) {
    const model = [...this.state.model];
    const newTags = model.slice();

    newTags.splice(currPos, 1);
    newTags.splice(newPos, 0, tag);

    // re-render
    this.setState({ model: newTags });
  }

  sendTags() {
    axios.get("https://api.github.com/users/mapbox", {}).then((response) => {
      console.log(response.data);
      console.log(response.status);
      console.log(response.statusText);
      console.log(response.headers);
      console.log(response.config);
    });
    const fd = new FormData();
    fd.append();
    axios.get(`http://127.0.0.1:5050/search`).then((res) => {
      const persons = res.data;
      this.setState({ persons });
    });
  }

  render() {
    const { tags, suggestions, placeholder, placeholder1, model } = this.state;
    return (
      <div>
        <ReactTags
          tags={model}
          placeholder={placeholder}
          suggestions={suggestions}
          handleDelete={this.handleDelete}
          handleAddition={this.handleAddition}
          handleDrag={this.handleDrag}
          delimiters={delimiters}
        />
        <ReactTags
          tags={tags}
          placeholder={placeholder1}
          handleDelete={this.m_handleDelete}
          handleAddition={this.m_handleAddition}
          handleDrag={this.m_handleDrag}
          delimiters={delimiters}
        />
      </div>
    );
  }
}

export default Input;
